import React from 'react';

function BlinkSettings({ settings, onChange }) {
  return (
    <div style={{ marginTop: '10px' }}>
      <label>
        Blink Duration (seconds):
        <input
          type="number"
          value={settings.duration}
          onChange={(e) => onChange({ ...settings, duration: parseFloat(e.target.value) })}
        />
      </label>
      <label>
        Blink Brightness (0 to 1):
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={settings.brightness}
          onChange={(e) => onChange({ ...settings, brightness: parseFloat(e.target.value) })}
        />
      </label>
      <label>
        Number of Blinks:
        <input
          type="number"
          value={settings.blinks}
          onChange={(e) => onChange({ ...settings, blinks: parseInt(e.target.value, 10) })}
        />
      </label>
    </div>
  );
}

export default BlinkSettings;
