import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { motion } from 'framer-motion';
import './Light.css';

function Light({ tlColor = 'red', onClick, count, settings }) {
  const [isBlinking, setIsBlinking] = useState(false);

  const handleBlink = () => {
    onClick(tlColor); // Викликається функція onClick, передана ззовні для збільшення лічильника
    setIsBlinking(true);
    setTimeout(() => {
      setIsBlinking(false);
    }, settings.duration * settings.blinks); // Моргає залежно від налаштувань
  };

  return (
    <motion.div
      className="light"
      onClick={handleBlink}
      style={{
        backgroundColor: isBlinking ? 'white' : tlColor,
        width: '100px',
        height: '100px',
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: 'white',
        fontSize: '20px'
      }}
      animate={{ opacity: isBlinking ? settings.brightness : 1 }}
      transition={{ duration: settings.duration / 1000, repeat: settings.blinks, repeatType: "reverse" }}
    >
      {count}
    </motion.div>
  );
}

Light.propTypes = {
  tlColor: PropTypes.string,
  onClick: PropTypes.func.isRequired,
  count: PropTypes.number.isRequired,
  settings: PropTypes.shape({
    duration: PropTypes.number,
    brightness: PropTypes.number,
    blinks: PropTypes.number
  }).isRequired
};

export default Light;
