import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { motion } from 'framer-motion';
import './Light.css';

function Light({ tlColor = 'red', onClick, count }) {
  const [isBlinking, setIsBlinking] = useState(false);

  const handleBlink = () => {
    onClick(tlColor); // Обробка кліку, збільшення лічильника
    setIsBlinking(true);
    setTimeout(() => {
      setIsBlinking(false);
    }, 300); // Моргає один раз за секунду
  };

  return (
    <motion.div
      className="light"
      onClick={handleBlink}
      style={{
        background: isBlinking ? "white" : tlColor,
        width: '100px',
        height: '100px',
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: 'white',
        fontSize: '20px'
      }}
      animate={{ opacity: isBlinking ? 0.5 : 1 }}
      transition={{ duration: 0.5, yoyo: Infinity }}
    >
      {count}
    </motion.div>
  );
}

Light.propTypes = {
  tlColor: PropTypes.string,
  onClick: PropTypes.func.isRequired,
  count: PropTypes.number.isRequired
};

export default Light;
