import React from 'react';
import Light from './Light';
import './TrafficLights.css';

const TrafficLights = ({ orientation, data, onLightClick }) => {
  // Перевіряємо, чи є дані та чи вони не порожні
  if (!data || data.length === 0) {
    return <div className="loading">Loading...</div>; // Відображення повідомлення під час завантаження
  }

  return (
    <div className={`trafficLightContainer ${orientation}`}>
      <div className="lightsContainer">
        {data.map((light) => (
          <Light
            key={light.id}
            tlColor={light.color}
            onClick={() => onLightClick(light.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default TrafficLights;
