import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { motion } from 'framer-motion';
import './Light.css';

function Light({ tlColor, onClick, settings }) {
  const [isBlinking, setIsBlinking] = useState(false);

  const handleBlink = () => {
    onClick(tlColor);
    setIsBlinking(true);
    const blinkTime = settings.duration / 1000 * settings.blinks;
    setTimeout(() => setIsBlinking(false), blinkTime);
  };

  return (
    <motion.div
      className="light"
      onClick={handleBlink}
      style={{
        backgroundColor: isBlinking ? 'white' : tlColor,
        width: '100px',
        height: '100px',
        borderRadius: '50%'
      }}
      animate={{ opacity: isBlinking ? settings.brightness : 1 }}
      transition={{ duration: settings.duration / 1000, repeat: settings.blinks, repeatType: "reverse" }}
    />
  );
}

Light.propTypes = {
  tlColor: PropTypes.string.isRequired,
  onClick: PropTypes.func.isRequired,
  settings: PropTypes.shape({
    duration: PropTypes.number.isRequired,
    brightness: PropTypes.number.isRequired,
    blinks: PropTypes.number.isRequired
  }).isRequired
};

export default Light;
