import React, { useState } from 'react';
import { motion } from 'framer-motion';
import BlinkSettings from './BlinkSettings'; // Упевніться, що шлях до компоненти правильний

function Light({ tlColor = 'red', onClick, count }) {
  const [isBlinking, setIsBlinking] = useState(false);
  const [settings, setSettings] = useState({
    duration: 0.5,
    brightness: 0.5,
    blinks: 3,
  });

  const handleBlink = () => {
    onClick(tlColor);
    let blinksLeft = settings.blinks;
    const interval = setInterval(() => {
      setIsBlinking(prev => !prev);
      blinksLeft--;
      if (blinksLeft <= 0) {
        clearInterval(interval);
        setIsBlinking(false);
      }
    }, settings.duration * 1000);
  };

  return (
    <div>
      <motion.div
        className="light"
        onClick={handleBlink}
        style={{
          background: isBlinking ? `rgba(255, 255, 255, ${settings.brightness})` : tlColor,
          width: '100px',
          height: '100px',
          borderRadius: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          color: 'white',
          fontSize: '20px'
        }}
        animate={{ opacity: isBlinking ? 0.5 : 1 }}
        transition={{ duration: settings.duration, yoyo: Infinity }}
      >
        {count}
      </motion.div>
      <BlinkSettings settings={settings} onChange={setSettings} />
    </div>
  );
}

export default Light;
