import React, { useState } from 'react';
import { motion } from 'framer-motion';

function Light({ tlColor, settings }) {
  const [isBlinking, setIsBlinking] = useState(false);

  const handleBlink = () => {
    setIsBlinking(true);
    let blinksLeft = settings.blinks;
    const interval = setInterval(() => {
      setIsBlinking(prev => !prev);
      blinksLeft--;
      if (blinksLeft <= 0) {
        clearInterval(interval);
        setIsBlinking(false);
      }
    }, settings.duration * 1000);
  };

  return (
    <motion.div
      className="light"
      onClick={handleBlink}
      style={{
        background: isBlinking ? `rgba(255, 255, 255, ${settings.brightness})` : tlColor,
        width: '100px',
        height: '100px',
        borderRadius: '50%'
      }}
      animate={{ opacity: isBlinking ? 0.5 : 1 }}
      transition={{ duration: settings.duration, repeat: Infinity, repeatType: "reverse" }}
    />
  );
}

export default Light;
