import React, { useState, useEffect, useCallback } from 'react';
import PropTypes from 'prop-types';
import { motion } from 'framer-motion';
import './Light.css';

function Light({ tlColor = 'red', onClick, settings = { duration: 250, brightness: 0.5, blinks: 1 } }) {
    const [isBlinking, setIsBlinking] = useState(false);

    // Функція, що викликається при кліку
    const handleBlink = useCallback(() => {
      onClick(tlColor);
      setIsBlinking(true);
      // Запускаємо моргання на заданий час
      setTimeout(() => setIsBlinking(false), settings.duration * settings.blinks);
    }, [tlColor, onClick, settings.duration, settings.blinks]);

    useEffect(() => {
      // Очищення таймера при анмаунті компоненти
      return () => clearTimeout(handleBlink);
    }, [handleBlink]);

    return (
      <motion.div
        className="light"
        onClick={handleBlink}
        style={{
          backgroundColor: tlColor,
          width: '100px',
          height: '100px',
          borderRadius: '50%'
        }}
        animate={{ opacity: isBlinking ? settings.brightness : 1 }}
        transition={{ duration: settings.duration / 1000, repeat: settings.blinks, repeatType: "reverse" }}
        onAnimationComplete={() => setIsBlinking(false)} // Викликається після завершення анімації
      />
    );
}

Light.propTypes = {
  tlColor: PropTypes.string,
  onClick: PropTypes.func.isRequired,
  settings: PropTypes.shape({
    duration: PropTypes.number,
    brightness: PropTypes.number,
    blinks: PropTypes.number
  }).isRequired
};

export default Light;
