import { motion } from "framer-motion";
import React, { useState } from "react";

function Light({ tlColor }) {
  const [isBlinking, setIsBlinking] = useState(false);

  const handleBlink = () => {
    setIsBlinking(true);
    setTimeout(() => {
      setIsBlinking(false);
    }, 1000); // Моргає один раз за секунду
  };

  return (
    <motion.div
      className="light"
      style={{
        background: isBlinking ? "white" : tlColor, // Білий колір для "моргання"
        width: '100px',
        height: '100px',
        borderRadius: '50%'
      }}
      whileTap={{ scale: 0.8 }}
      onClick={handleBlink}
      animate={{ opacity: isBlinking ? 0.5 : 1 }}
      transition={{ duration: 0.5, yoyo: Infinity }}
    />
  );
}

export default Light;
