import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { motion } from 'framer-motion';
import './Light.css';

function Light({ tlColor = 'red', onClick, count }) {
  const [isBlinking, setIsBlinking] = useState(false);
  const [blinkSettings, setBlinkSettings] = useState({
    duration: 0.5, // тривалість одного моргання в секундах
    brightness: 0.5, // яскравість кольору при морганні
    blinks: 3, // кількість моргань
  });

  const handleBlink = () => {
    onClick(tlColor); // Обробка кліку, збільшення лічильника
    setIsBlinking(true);
    let blinksLeft = blinkSettings.blinks;
    const interval = setInterval(() => {
      setIsBlinking(prev => !prev);
      blinksLeft--;
      if (blinksLeft <= 0) {
        clearInterval(interval);
        setIsBlinking(false);
      }
    }, blinkSettings.duration * 1000);
  };

  return (
    <div>
      <motion.div
        className="light"
        onClick={handleBlink}
        style={{
          background: isBlinking ? `rgba(255, 255, 255, ${blinkSettings.brightness})` : tlColor,
          width: '100px',
          height: '100px',
          borderRadius: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          color: 'white',
          fontSize: '20px'
        }}
        animate={{ opacity: isBlinking ? 0.5 : 1 }}
        transition={{ duration: blinkSettings.duration, yoyo: Infinity }}
      >
        {count}
      </motion.div>
      <div style={{ marginTop: '10px' }}>
        <label>
          Blink Duration:
          <input
            type="number"
            value={blinkSettings.duration}
            onChange={(e) => setBlinkSettings({ ...blinkSettings, duration: parseFloat(e.target.value) })}
          />
        </label>
        <label>
          Blink Brightness:
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={blinkSettings.brightness}
            onChange={(e) => setBlinkSettings({ ...blinkSettings, brightness: parseFloat(e.target.value) })}
          />
        </label>
        <label>
          Number of Blinks:
          <input
            type="number"
            value={blinkSettings.blinks}
            onChange={(e) => setBlinkSettings({ ...blinkSettings, blinks: parseInt(e.target.value, 10) })}
          />
        </label>
      </div>
    </div>
  );
}

Light.propTypes = {
  tlColor: PropTypes.string,
  onClick: PropTypes.func.isRequired,
  count: PropTypes.number.isRequired
};

export default Light;
